import React from 'react';
import styles from './Construction.module.css'; // Import the CSS module

const Construction = () => {
  const flashcardData = {
    imgSrc: "path-to-your-image.jpg", // Replace with your actual image path
    title: "Modular Kitchen",
    description: "Functional Kitchen, wardrobe, and storage"
  };

  return (
    <div>
      <h1>One-stop shop for all construction and interiors</h1>
      <h2>Be it end-to-end construction, interior or modular solutions, we have it all for your home or office. With a wide range of furniture & decor, we have your back from start to finish.</h2>
      <div className={styles.flashcardContainer}>
        {Array.from({ length: 4 }).map((_, index) => (
          <div key={index} className={styles.flashcard}>
            <img src={flashcardData.imgSrc} alt={flashcardData.title} className={styles.image}/>
            <h3>{flashcardData.title}</h3>
            <p>{flashcardData.description}</p>
            <div className={styles.forwardIcon}>&gt;</div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Construction;
