import React from 'react';
import styles from './Construction.module.css';
import flashcard1 from '../assets/flashcard1.jpg';
import flashcard2 from '../assets/flashcard2.jpg';
import flashcard3 from '../assets/flashcard3.jpg';
import flashcard4 from '../assets/flashcard4.jpg';

const Construction = () => {
  const flashcards = [
    {
      imgSrc: flashcard1,
      title: "Modular Kitchen",
      description: "Functional kitchen with optimized storage and modern design."
    },
    {
      imgSrc: flashcard2,
      title: "Full Home Interior",
      description: "Latest interior solutions for your home."
    },
    {
      imgSrc: flashcard3,
      title: "End to End Construction",
      description: "Latest home solutions."
    },
    {
      imgSrc: flashcard4,
      title: "Luxury Interiors",
      description: "Latest home solutions."
    }
  ];

  return (
    <div className={styles.container}>
      <h1>One-stop shop for all construction and interiors</h1>
      <p>{"Be it end-to-end construction, interior or modular solutions, we have it all for your home or office.\nWith a wide range of furniture & decor, we have your back from start to finish."}</p>
      <div className={styles.flashcardContainer}>
        {flashcards.map((card, index) => (
          <div key={index} className={styles.flashcard}>
            <img src={card.imgSrc} alt={card.title} className={styles.image}/>
            <h3>{card.title}</h3>
            <p>{card.description}</p>
            <div className={styles.forwardIcon}>&gt;</div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Construction;
