import React, { useState } from 'react';
import Houseimage from '../assets/Houseimage.jpeg'; // Ensure this path is correct
import styles from './Container1.module.css'; // Ensure this path is correct

const Container1 = () => {
    const [formData, setFormData] = useState({ name: '', phone: '', location: '' });
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');

    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const validateForm = () => {
        // Basic validation check for empty fields
        return formData.name && formData.phone && formData.location;
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        if (!validateForm()) {
            setError('Please fill in all fields.');
            return;
        }

        setIsLoading(true);
        setError(''); // Clear any previous errors

        try {
            const response = await fetch('http://localhost:3001/appointments', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData)
            });

            if (!response.ok) {
                throw new Error('Failed to book appointment');
            }

            const result = await response.json();
            alert(`Appointment booked successfully: ${JSON.stringify(result.appointment)}`);
            setFormData({ name: '', phone: '', location: '' }); // Reset form
        } catch (error) {
            console.error('Error booking appointment:', error);
            setError(error.message || "Error sending data to server");
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className={styles.container1}>
            <div className={styles.leftContainer1} style={{ backgroundImage: `url(${Houseimage})` }}>
                <div className={styles.content}>
                    <h1 className={styles.contentTitle1}>Construct Your Dream Home</h1>
                    <p>Hassle-free Interiors and Construction from start to finish.</p>
                    <button className={styles.button1}>Start Your Construction</button>
                    <div className={styles.popularOptions}>
                        <p>Popular:</p>
                        <button className={styles.button2}>Interior Designing</button>
                        <button className={styles.button2}>End to End Construction</button>
                    </div>
                </div>
            </div>
            <div className={styles.rightContainer1}>
                <form onSubmit={handleSubmit} className={styles.form}>
                    <div className={styles.formGroup}>
                        <label htmlFor="name">Name</label>
                        <input id="name" type="text" name="name" placeholder="Your Name" value={formData.name} onChange={handleChange} />
                    </div>
                    <div className={styles.formGroup}>
                        <label htmlFor="phone">Mobile Number</label>
                        <input id="phone" type="tel" name="phone" placeholder="Your Mobile Number" value={formData.phone} onChange={handleChange} />
                    </div>
                    <div className={styles.formGroup}>
                        <label htmlFor="location">Location</label>
                        <input id="location" type="text" name="location" placeholder="Location of Your Plot" value={formData.location} onChange={handleChange} />
                    </div>
                    {error && <div className={styles.error}>{error}</div>}
                    <button type="submit" className={styles.submitButton} disabled={isLoading}>
                        {isLoading ? 'Booking...' : 'Book Now'}
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Container1;
