// Construction.js
import React, { useState } from 'react';
import styles from './Construction.module.css';
import flashcard1 from '../assets/flashcard1.jpg';
import flashcard2 from '../assets/flashcard2.jpg';
import flashcard3 from '../assets/flashcard3.jpg';
import flashcard4 from '../assets/flashcard4.jpg';

const Construction = () => {
  const flashcards = [
    { imgSrc: flashcard1, title: "Modular Kitchen", description: "Functional kitchen with modern design." },
    { imgSrc: flashcard2, title: "Full Home Interior", description: "Latest interior solutions for your home." },
    { imgSrc: flashcard3, title: "End to End Construction", description: "Latest home solutions." },
    { imgSrc: flashcard4, title: "Luxury Interiors", description: "Latest home solutions." },
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  const navigateLeft = () => {
    setCurrentIndex((prevIndex) => prevIndex > 0 ? prevIndex - 1 : flashcards.length - 1);
  };

  const navigateRight = () => {
    setCurrentIndex((prevIndex) => prevIndex < flashcards.length - 1 ? prevIndex + 1 : 0);
  };

  return (
    <div className={styles.container}>
      <h1>One-stop shop for all construction and interiors</h1>
      <p>Be it end-to-end construction, interior or modular solutions, we have it all for your home or office. With a wide range of furniture & decor, we have your back from start to finish.</p>
      <div className={styles.arrowButton + " " + styles.left} onClick={navigateLeft}>&lt;</div>
      <div className={styles.arrowButton + " " + styles.right} onClick={navigateRight}>&gt;</div>
      <div className={styles.flashcardContainer}>
        <div className={styles.flashcard}>
          <div className={styles.imageContainer} style={{ backgroundImage: `url(${flashcards[currentIndex].imgSrc})` }}>
          </div>
          <div className={styles.textContainer}>
            <h3 className={styles.flashcardTitle}>{flashcards[currentIndex].title}</h3>
            <p className={styles.flashcardDescription}>{flashcards[currentIndex].description}</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Construction;
